import "./App.css";
import React from 'react';
import algoliasearch from "algoliasearch/lite";
import {
  InstantSearch,
  SearchBox,
  Index, Hits,
  useConfigure
} from "react-instantsearch";

import {
  APP_ID,
  API_KEY,
  INDICES, INDICES_COLUMN_NAMES, INDICES_ALPHA,
  HIT_URL_REF, HIT_PER_PAGE, TYPO_TOLERANCE
} from "./config.js";

import { Popover, OverlayTrigger, Badge } from 'react-bootstrap';


const searchClient = algoliasearch(APP_ID, API_KEY);

// {/* <Popover.Header as="h3">Popover right</Popover.Header> */}

function RankingInfo({ ri }) {
  return (
    Object.keys(ri).map((key, i) => (
      <p key={i}>
        <span>Key Name: {key}</span>
        <span>Value: {ri.object[key]}</span>
      </p>
    )
    ))
}
function Hit({ hit }) {
  const ri = hit._rankingInfo;
  const popover = (
    <Popover>
      <Popover.Body>
        <RankingInfo ri={ri} />

        // <table class="stl-w-full">
        //   <thead>
        //     <tr className="">
