import "./App.css";
import React from 'react';
import algoliasearch from "algoliasearch/lite";
import {
  InstantSearch,
  SearchBox,
  Index, Hits,
  useConfigure
} from "react-instantsearch";

import {
  APP_ID,
  API_KEY,
  INDICES, INDICES_COLUMN_NAMES, INDICES_ALPHA,
  HIT_URL_REF, HIT_PER_PAGE, TYPO_TOLERANCE
} from "./config.js";

import { Popover, OverlayTrigger, Badge, Card } from 'react-bootstrap';


const searchClient = algoliasearch(APP_ID, API_KEY);

// {/* <Popover.Header as="h3">Popover right</Popover.Header> */}

function RankingInfo({ ri }) {
  return (
    Object.keys(ri).map((key, i) => (
      <p key={i}>
        <span>{key}: </span>
        <span>{JSON.stringify(ri[key])}</span>
      </p>
    )
    ))
}
function Hit({ hit }) {
  const ri = hit._rankingInfo;
  const popover = (
    <Popover>
      <Popover.Body>
        <RankingInfo ri={ri} />
      </Popover.Body>
    </Popover>
  );
  // keywordScore
  // neuralScore
  // semanticScore
  // userScore
  // mergeInfo: {keyword, semantic}

  // <table class="stl-w-full">
  //   <thead>
  //     <tr className="">
