  //       </td>
  //     </tr>
  //   </tbody>
  // </table>
  let badgeBg = "";
  let badgeLabel = "";
  if (ri.mergeInfo && ri.mergeInfo['keyword'] !== undefined) {
    badgeBg = "danger";
    badgeLabel = "Keyword";
  }
  if (ri.mergeInfo && ri.mergeInfo['semantic'] !== undefined) {
    badgeBg = "primary"
    badgeLabel = "Vector";
  }
  if (ri.mergeInfo && ri.mergeInfo['keyword'] !== undefined && ri.mergeInfo['semantic'] !== undefined) {
    badgeBg = "warning";
    badgeLabel = "Keyword & Vector";
  }

  return (

    <Card  >
      <Card.Header className="text-truncate">
        <h5 className="text-truncate d-flex justify-content-center">
          {hit.productDisplayName}
        </h5>
        <Badge className="float-end" bg={badgeBg}>{badgeLabel}</Badge>
      </Card.Header>
      <Card.Body>
        <p className="card-text text-truncate">{hit.categories[0].displayName}</p>
        <img src={`${HIT_URL_REF}${hit.frontView}`} className="hit-image" alt="" />
      </Card.Body>
      <Card.Footer className="text-body-secondary">
        <span>{hit.position.index + 1} - {hit.objectID}</span>
        <OverlayTrigger trigger="hover" placement="bottom" overlay={popover}>
          <Badge className="bg-secondary float-end">Info</Badge>
        </OverlayTrigger>
      </Card.Footer>

    </Card>
  );
}

const transformItems = (items, { results }) => {
  return items.map((item, index) => ({
    ...item,
    position: { index, page: results.page },
